import { Component, OnInit} from '@angular/core';
import { CartService } from '../cart.service';
import { Livres } from '../models/livres';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {
  cartItems: Livres[] = [];
  cartTotal: number = 0;

  constructor(private cartService: CartService) { }

  ngOnInit() {
    this.cartItems = this.cartService.getItems();
    this.cartTotal = this.cartService.getTotal();
  }

  removeFromCart(livre: Livres): void {
    this.cartService.removeFromCart(livre);
    this.cartItems = this.cartService.getItems();
    this.cartTotal = this.cartService.getTotal();
  }

  clearCart(): void {
    this.cartService.clearCart();
    this.cartItems = [];
    this.cartTotal = 0;
  }
}
<div class="container mt-4" style="padding: 2;">
  <div class="row justify-content-center">
    <div class="col-md-8">
      <div *ngIf="cartItems.length > 0" class="mb-4">
        <h2 class="mb-3" style=" font-size : 19px; font-family:Georgia, 'Times New Roman', Times, serif">Articles dans le panier</h2>
        <ul class="list-group">
          <li *ngFor="let item of cartItems" class="list-group-item d-flex justify-content-between align-items-center">
            <span style="color: #abae5b; font-size : 19px; font-family:Georgia, 'Times New Roman', Times, serif"  >{{ item.titre }} - {{ item.prixQuantite.prix | currency }}</span>
            <button class="btn btn-sm btn-danger" (click)="removeFromCart(item)" >Supprimer</button>
          </li>
        </ul>
        <p class="mt-3">Total: {{ cartTotal | currency }}</p>

        <div class="d-flex justify-content-between"> <!-- Utilizando flexbox para alinhar os botões -->
          <a routerLink="/livres" class="btn btn-secondary">Retourner</a>
          <button class="btn btn-primary" (click)="clearCart()" style="background-color:#abae5b; border:#abae5b">Vider le panier</button>
        </div>
      </div>
      <div *ngIf="cartItems.length === 0">
        <p class="text-center">Panier vide.</p>
        <a routerLink="/livres" class="btn btn-secondary">Retourner</a>
      </div>
    </div>
  </div>
</div>
