import { Component, OnInit } from '@angular/core';
import { LivresService } from '../../services/livres.service';
import { Livres } from '../../models/livres';

@Component({
  selector: 'app-livres-component',
  templateUrl: './livres.component.html',
  styleUrls: ['./livres.component.css']
})
export class LivresComponent implements OnInit {

  livres: Livres[] = [];

  constructor(private livresService: LivresService) { }

  ngOnInit(): void {
    this.livresService.getLivres().subscribe(livres => {
      this.livres = livres;
    });
  }
}
<div class="container" >
  <div class="row" style="padding-top: 10%">
    
    <ng-container *ngFor="let livre of livres; let i = index">
      <div class="col-md-3" style="padding-bottom:5%">
          <div class="card" >
          
          <img src=".././assets/images/{{livre.image}}"  style="max-height: 350px;" class="card-img-top" alt="{{ livre.titre }}">
          <div class="card-body" >
            <h5 class="card-title" style="color: #abae5b; font-size : 19px; font-family:Georgia, 'Times New Roman', Times, serif">{{ livre.titre }}</h5>
            <p class="card-text">
        
            </p>
          </div>
          <ul class="list-group list-group-flush">
            <li class="list-group-item" style="border: none;"> <strong style=" font-size : 14px; font-family:Georgia, 'Times New Roman', Times, serif">Auteur:</strong> {{ livre.auteur }}<br></li>
            <!-- <li class="list-group-item"> <strong>Descrição:</strong> {{ livre.description }}<br></li> -->
            <li class="list-group-item" style="border: none;"> <strong style=" font-size : 14px; font-family:Georgia, 'Times New Roman', Times, serif">Prix: </strong> {{ livre.prixQuantite.prix  }}<strong> $</strong><br></li>
            <li class="list-group-item" style="border: none;"><a [routerLink]="['/livredetails', livre.code]" class="btn btn-primary" style="ri">Details</a></li>
            <li class="list-group-item" style="border: none;"></li>
          </ul>
        </div>
      </div>
    </ng-container>
  </div>
</div>
